                    }
                    $pcd_stm[0]['hepatit_b'] .= '</li>';
                }
                $pcd_stm[0]['hepatit_b'] .= '</ul>';
            }

            if(!empty($pcd_stm[0]['tuberculosis_opt'])){
                $pcd_stm[0]['tuberculosis'] = '<ul class="ChildrenDiseases_pm">';
