                    if($km > 0 ){
                        $pcd_stm[0]['tuberculosis'] .= '';
                    }
                    $pcd_stm[0]['tuberculosis'] .= '<li>'.$tuberculosis[$vm];
                    if($vm == 4 && !empty($pcd_stm[0]['tuberculosis_text']) ){
                        $pcd_stm[0]['tuberculosis'] .= ': '.$pcd_stm[0]['tuberculosis_text'];
                    }
                    $pcd_stm[0]['tuberculosis'] .= '</li>';
                }
