            }
            
            if(!empty($pcd_stm[0]['hpv_opt'])){
                $pcd_stm[0]['hpv'] = '<ul class="ChildrenDiseases_pm">';
                $hpv_opt_arr = explode(",", $pcd_stm[0]['hpv_opt']);
                foreach($hpv_opt_arr as $km => $vm){
                    if($km > 0 ){
                        $pcd_stm[0]['hpv'] .= '';
                    }
