                    if($vm == 9 && !(empty($pcd_stm[0]['hpv_last_vaccination']) || $pcd_stm[0]['hpv_last_vaccination'] == "0000-00-00 00:00:00" || $pcd_stm[0]['hpv_last_vaccination'] == "1970-01-01 00:00:00")){
