class PatientCourseOptions extends BasePatientCourseOptions
{

    public function getCourseDataOptions($ipid)
    {
        
        $logininfo = new Zend_Session_Namespace('Login_Info');
        $userid = $logininfo->userid;
        $clientid = $logininfo->clientid;
        
        
        $course = Doctrine_Query::create()
        ->select('*')
        ->from('PatientCourseOptions indexBy patient_course_id')
        ->where('ipid = ?', $ipid);
        $csarr = $course->fetchArray();
        
        return $csarr;
        
    }
    
}