class PatientDatamatrixImport extends BasePatientDatamatrixImport
{

	private static $mandatory_columns = array(
			'ipid',
			'datamatrix_xml',
	);
	
	public function set_new_record($params = array())
	{
	
		if (empty($params) || !is_array($params)) {
			return false;// something went wrong
		}
		
		foreach (self::$mandatory_columns as $column) {
	
			if ( empty($params[$column]) &&  empty($this->{$column}) )
			{
				return false;
			}
		}
	
		foreach ($params as $k => $v)
			if (isset($this->{$k})) {
				$this->{$k} = $v;
			}
	
		$this->save();
	
		return $this->id;
	
	}
	
	
	public function delete_row( $id = null )
	{
		if (( ! is_null($id)) && ($obj = $this->getTable()->find($id)))
		{
			$obj->delete();
			return true;
	
		} else {
			return false;
		}
	}
	
	
	
	
	
	public function getByIpid( $ipid = array()) {
		
		$result = false;
		
		if ( empty($ipid)) {
			return $result;
		}
		
		if ( ! is_array($ipid)){
			throw new Exception( __METHOD__ . ' : param must be array');
		}
		
		$q = $this->getTable()->createQuery()
		->select('*') //optional select for readability
		->whereIn("ipid", $ipid)
		->fetchArray();
		
		
		if ($q) {
			
			$result = array();
			
			foreach ($q as $row) {
				
				$result[$row['ipid']][] = $row;
			}
				
		}
		
		return $result;		
		
	}
	
}