class PatientFamilySupportService extends BasePatientFamilySupportService
{            

    public function getPatientFamilySupportService($ipid)
    {
        if (empty($ipid)) {
            return; //fail-safe
        }
        
        $pfss = Doctrine_Query::create()
        ->select("*")
        ->from('PatientFamilySupportService')
        ->andWhere("ipid= ? ", $ipid);
        $pfssarr = $pfss->fetchArray();
        
        return $pfssarr;
        
    }

    
}

