class PatientPlaygroup extends BasePatientPlaygroup
{            

    public function getPatientPlaygroup($ipid)
    {
        if (empty($ipid)) {
            return; //fail-safe
        }
        
        $pg = Doctrine_Query::create()
        ->select("*")
        ->from('PatientPlaygroup')
        ->andWhere("ipid= ? ", $ipid);
        $pgarr = $pg->fetchArray();
        
        return $pgarr;
        
    }

    public static function getLastVisitPlaygroup()
    {
        $last_visit = array(
            '1' => "Date",
            '2' => "Regelmäßig",
            '3' => "Unregelmäßig",
            '4' => "Unbekannt",
            '5' => "Sonstiges",
        );
        
        return $last_visit;
    }
}

