{            

    public function getPatientSchool($ipid)
    {
        if (empty($ipid)) {
            return; //fail-safe
        }
        
        $pg = Doctrine_Query::create()
        ->select("*")
        ->from('PatientSchool')
        ->andWhere("ipid= ? ", $ipid);
        $pgarr = $pg->fetchArray();
        
        return $pgarr;
        
    }

