 * @method findOrCreateOneBy($fieldName = '', $value = null, array $data = array())
 * 
 */
class FormBlockOrganicEntriesExitsTable extends Pms_Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object FormBlockOrganicEntriesExitsTable
     */
	
    public static function getInstance()
    {
        return Doctrine_Core::getTable('FormBlockOrganicEntriesExits');
    }
    
    public function findlastevent( $contact_form_id = '', $ipid = '', $hydrationMode = Doctrine_Core::HYDRATE_RECORD )
    {
    	if (empty($ipid) || ! is_string($ipid)) {
    
    		return;
    
    	} else {
    		return self::getInstance()->createQuery('enq')
    		->where('ipid = ?')
    		->andWhere('contact_form_id = ?')
    		->orderBy('id DESC') // just in case the delete is not ok
    		->limit(1)
    		->fetchOne(array($ipid, $contact_form_id), $hydrationMode);
    	}
    }
   
}