class FormBlockProblemsTable extends Pms_Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return FormBlockProblemsTable (object)
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('FormBlockProblems');
    }
    
    
    public static function find_patient_problems($ipids = array())
    {
        if (empty($ipids)) {
            return;
        }
        
        $poa = self::getInstance()->createQuery('pp INDEXBY id')
        ->select('pp.*,pps.*')
        ->whereIn('pp.ipid', $ipids);
        $poa->leftJoin('pp.FormBlockProblemsSituations as pps')
        ->orderBy('pp.problem_order ASC');
        $result = $poa->fetchArray();
        
        return $result;
    }
    
    
}