class Hl7MessagesReceivedTable extends Pms_Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return Hl7MessagesReceivedTable (object)
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('Hl7MessagesReceived');
    }
    
    

    public static function findByIpid($ipid = '', $hydrationMode = Doctrine_Core::HYDRATE_RECORD)
    {
        
        return self::getInstance()->createQuery('hl7_mr')
        ->select('hl7_mp.*, hl7_mr.*')
        ->leftJoin("hl7_mr.Hl7MessagesProcessed hl7_mp")
        ->where("hl7_mp.ipid = ? ", $ipid)
        ->execute(null, $hydrationMode)
        ;
    }
    
   
    
}