class OrganicEntriesExitsSetsTable extends Pms_Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object FormBlockOrganicEntriesExitsTable
     */
	
    public static function getInstance()
    {
        return Doctrine_Core::getTable('OrganicEntriesExitsSets');
    }
    //ISPC-2661 Carmen
    public function findset($id, $hydrationMode = Doctrine_Core::HYDRATE_RECORD )
    {
    	
    		return self::getInstance()->createQuery('enq')
    		->select("*")
    		->where('id = ? ', $id)
    		->fetchOne();
    	
    }
    //--
    
    public static function findlastid_by_ipid($ipid = 0,$hydrationMode = Doctrine_Core::HYDRATE_RECORD )
    {
        if (empty($ipid)) {
            return false;
        }
        
        $open_sets = self::getInstance()->createQuery()
        ->select("*")
        ->where('ipid = ? ', $ipid)
        ->orderBy('id DESC') // just in case the delete is not ok
        ->limit(1)
        ->fetchOne();
        
        if ( ! empty($open_sets)) {
            return $open_sets ;
        } else {
            return array();
        }
        
    }
    public static function find_open_by_ipid($ipid = 0, $hydrationMode = Doctrine_Core::HYDRATE_RECORD  )
    {
        if (empty($ipid)) {
            return false;
        }
        
 
        $open_sets = self::getInstance()->createQuery()
        ->select("*")
        ->where('ipid = ? ', $ipid)
        ->andWhere('endset = ? ', "0")
        ->limit(1)
        ->fetchOne();
        
        if ( ! empty($open_sets)) {
            return $open_sets->toArray();
        } else {
            return array();
        }
        
        
        
    }
}