class PatientDemstepcareTable extends Pms_Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return PatientDemstepcareTable (object)
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('PatientDemstepcare');
    }
    
    
    /**
     * @auth Ancuta
     * @param number $ipid
     * @param number $form_id
     * @return void|mixed
     */
    public static function find_patient_form_By_Form_Id($ipid = 0, $form_id = 0)
    {
        if (empty($ipid) && empty($form_id)) {
            return;
        }
        return self::getInstance()->createQuery('pd')
        ->select('pd.*')
        ->where('pd.ipid =?', $ipid)
        ->andWhere('pd.id =? ', $form_id)
        ->limit(1)
        ->fetchOne(null, Doctrine_Core::HYDRATE_ARRAY);
        ;
    }
    
    
    public static function find_form_By_patient($ipid = 0)
    {
        if (empty($ipid)) {
            return;
        }
        return self::getInstance()->createQuery('pd')
        ->select('pd.*')
        ->where('pd.ipid =?', $ipid)
        ->limit(1)
        ->fetchOne(null, Doctrine_Core::HYDRATE_ARRAY);
        ;
    }
    
    public static function find_all_forms_By_patients($ipids = array())
    {
        if (empty($ipids)) {
            return;
        }
        
        
        return self::getInstance()->createQuery('pd')
        ->select('*')
        ->whereIn('ipid =?', $ipids)
        ->fetchArray();
        ;
    }
}
    