class PharmaPatientDrugplanRequestsTable extends Pms_Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return PharmaPatientDrugplanRequestsTable (object)
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('PharmaPatientDrugplanRequests');
    }

    /**
     * @author Ancuta 
     * 08.02.2020
     * @param string $ipid
     * @param number $user
     * @param boolean $request_status
     * @return void|array|Doctrine_Collection
     */
    public static function find_patient_user_requests($ipid = '', $user = 0, $request_status = false)
    {
        if (empty($ipid) && empty($user)) {
            return;
        }

        $q = self::getInstance()->createQuery()
        ->select('*')
        ->where('ipid =?', $ipid)
        ->andWhere('user =? ', $user);
        if ($request_status) {
            $q->andWhere('status =? ', $request_status);
        }
        $q->orderBy('create_date DESC');
        $q->limit(1);
        $result = $q->fetchArray();


        return $result;
    }
    
    
    public static function find_request_by_ids($request_ids = array(),$status = false) {
        
        if(empty($request_ids )  ){
            return array();
        }
/*         $ppdr = self::getInstance()->createQuery('ppdr')
        ->select('ppdr.*')
        ->whereIn('ppdr.id', $request_ids);
        
        if (! empty($status)) {
