        return $result; */
        
        
        $ppdr = self::getInstance()->createQuery('ppdr')
        ->select('ppdr.*,ppd.*,ppdd.*,ppde.*')
        ->whereIn('ppdr.id', $request_ids);
        
        if (! empty($status)) {
            $ppdr->andWhere('ppdr.status = ? ', $status);
        }
        $ppdr->leftJoin('ppdr.PharmaPatientDrugplan as ppd')
        ->leftJoin('ppd.PharmaPatientDrugplanDosage as ppdd')
        ->leftJoin('ppd.PharmaPatientDrugplanExtra as ppde')
        ;
//         dd($ppdr->getSqlquery());
        $result = $ppdr->fetchArray();
        
        return $result;
        
        
        
    }
    
    
    
    
    
    
    /**
     * @author Ancuta
     * @param string $ipid
     * @param boolean $user
     * @param boolean $request_status
     * @return void|array|Doctrine_Collection
     * ISPC-2507  Ancuta 19.02.2020 
