     */
    public static function find_patient_requests($ipid = '', $user = false, $request_status = false )
    {
        if (empty($ipid)) {
            return;
        }
        
        $q = self::getInstance()->createQuery()
        ->select('*')
        ->where('ipid =?', $ipid);
        
        if ($user) {
            $q->andWhere('user =? ', $user);
        }
        if ($request_status) {
            $q->andWhere('status =? ', $request_status);
        }
        $q->orderBy('create_date DESC');
        $result = $q->fetchArray();
        
        
        return $result;
    }
    
    
    
    
    public static function find_patient_NotProessed_requests($ipid = '', $user = false, $request_status = false,$request_id = false )
    {
        if (empty($ipid)) {
            return;
        }
        
//         $q = self::getInstance()->createQuery()
//         ->select('*')
//         ->where('ipid =?', $ipid);
        
//         if ($user) {
//             $q->andWhere('user =? ', $user);
//         }
//         if ($request_status) {
//             $q->andWhere('status =? ', $request_status);
//         }
//         $q->orderBy('create_date DESC');
//         $result = $q->fetchArray();
        
        
        $ppdr = self::getInstance()->createQuery('ppdr')
        ->select('ppdr.*,ppd.*,ppdd.*,ppde.*,ppr.*')
        ->where('ipid =?', $ipid);
        
        if ($user) {
            $ppdr->andWhere('user =? ', $user);
        }
        if ($request_status) {
            $ppdr->andWhere('status =? ', $request_status);
        }
        if ($request_id) {
            $ppdr->andWhere('id =? ', $request_id);
        }
        $ppdr->leftJoin('ppdr.PharmaPatientRequests as ppr');
        $ppdr->andWhere('ppr.processed = "no"');
        $ppdr->leftJoin('ppdr.PharmaPatientDrugplan as ppd')
        ->leftJoin('ppd.PharmaPatientDrugplanDosage as ppdd')
        ->leftJoin('ppd.PharmaPatientDrugplanExtra as ppde')
        ;
        
        
        
        $result = $ppdr->fetchArray();
        
        return $result;
    }
    
    
