class PrintJobsItemsTable extends Pms_Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return PrintJobsItemsTable (object)
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('PrintJobsItems');
    }

    
    /**
     * ISPC-2609 Ancuta 10.09.2020
     * @param number $job_id
     * @return boolean
     */
    public static function __checkjobsforitems($job_id = 0)
    {
        if (empty($job_id)) {
            return false;
        }

        $q = self::getInstance()->createQuery()
            ->select('*')
            ->where('print_job_id =?', $job_id);
        //$q->andWhere('status = ?', "new");
        $q->andWhere('status = "new" OR (status = "error" AND tries < 10) ');
        $result = $q->fetchArray();
 
        if (! empty($result)) {
            return true;
        } else {
            return false;
        }
    }
    
    /**
     * 
     * ISPC-2609 Ancuta 10.09.2020
     * @param number $job_id
     * @return boolean
     */
    public static function __getnextitem($job_id = 0)
    {
        if (empty($job_id)) {
            return false;
        }

        $q = self::getInstance()->createQuery()
            ->select('*')
            ->where('print_job_id =?', $job_id);
        $q->andWhere('status = ?', "new");
        $q->orderBy('create_date ASC');
        $q->limit(1);
        $result = $q->fetchOne(array(), Doctrine_Core::HYDRATE_ARRAY);
 
        
        if (! empty($result)) {
            return $result['id'];
        } else {
            return false;
        }
    }
    
    /**
     * ISPC-2609 Ancuta 11.09.2020
     * @param number $job_id
     * @param number $items_nr
     * @return boolean
     */
    public static function __getnextitems($job_id = 0,$items_nr = 10)
    {
        if (empty($job_id)) {
            return false;
        }

        if(!isset($items_nr) || $items_nr == 0 ){
            $items_nr = 10;
        }
        
        $q = self::getInstance()->createQuery()
            ->select('*')
            ->where('print_job_id =?', $job_id);
        //$q->andWhere('status = ?', "new");
        $q->andWhere('status = "new" OR (status = "error" AND tries < 10) ');
        $q->orderBy('create_date ASC');
        $q->limit($items_nr);
        $result = $q->fetchArray();
 
        
        if (! empty($result)) {
            return $result;
        } else {
            return false;
        }
    }
}