class ReceiptPrintSettingsTable extends Pms_Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return ReceiptPrintSettingsTable (object)
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('ReceiptPrintSettings');
    }
    
    /**
     *
     * @param mixed $hydrationMode
     * @return void|Doctrine_Collection
     */
    public static function findAllClientReceiptPrintSettings($clientid, $hydrationMode = Doctrine_Core::HYDRATE_ARRAY)
    {
    	return self::getInstance()->createQuery('rps')
    	->select("rps.*")
    			->where('rps.clientid = ? ', $clientid)
    			->execute(null, $hydrationMode);
    }
    
    public static function findClientReceiptPrintSettingsbyId($idsarr,$clientid, $hydrationMode = Doctrine_Core::HYDRATE_ARRAY)
    {
    	return self::getInstance()->createQuery('rps')
    	->select("rps.*")
    	->whereIn('rps.id', $idsarr)
    	->andWhere('rps.clientid = ? ', $clientid)
    	->execute(null, $hydrationMode);
    }
}