class SurveyEmailSettingsTable extends Pms_Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return SurveyEmailSettingsTable (object)
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('SurveyEmailSettings');
    }
    
    /**
     *
     * @param mixed $hydrationMode
     * @return void|Doctrine_Collection
     */
    public static function findByClientid($clientid, $hydrationMode = Doctrine_Core::HYDRATE_ARRAY)
    {
    	return self::getInstance()->createQuery('ils')
    	->select("ils.*")
    	->where('ils.clientid = ? ', $clientid)
